package ugo.sp1.apr18;

/**
 * @author sharath
 *
 */
public class primitivedatatype {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 100, i = -50;
		char  name = 't';
		boolean b	= true;
		byte  y = -126;
		float	f	=	2530.32f;
		double	d	=	2533.32d;
		short	s	=	1024;
		long	l	= 253215;
		
		
		System.out.println("print the primitive data type: int :" +a  + "\t" +i);
		System.out.println("print the primitive data type: char:" +name);
		System.out.println("print the primitive data type: boolean:" +b);
		System.out.println("print the primitive data type: byte:" +y);
		System.out.println("print the primitive data type: float:" +f);
		System.out.println("print the primitive data type: double:" +d);
		System.out.println("print the primitive data type: short:" +s);
		System.out.println("print the primitive data type: long:" +l);
	}

}
