package sp2apr20;

public class ComparisionOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a , b;
	        a = 5;
	        b = (a == 6) ? 10: 30;
	        System.out.println( "Value of b is : " +  b  );
	        System.out.println( "Value of b is : " +  a  );

	        b = (a == 5) ? 10: 30;
	        System.out.println( "Value of b is : " + b );
	        System.out.println( "Value of b is : " +  a  );
	}

}
